'use strict';

/**
 * Convert base64 data url string to blob.
 * @param {string} base64 - The data string to convert.
 * @param {string} [contentType] - Used in Blob constructor.
 * @returns {Blob}
 */
module.exports = function (base64) {
  var contentType = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : '';


  var sliceSize = 1024;
  var byteCharacters = atob(base64);
  var slicesCount = Math.ceil(byteCharacters.length / sliceSize);
  var byteArrays = new Array(slicesCount);

  for (var sliceIndex = 0; sliceIndex < slicesCount; ++sliceIndex) {
    var begin = sliceIndex * sliceSize;
    var end = Math.min(begin + sliceSize, byteCharacters.length);
    var bytes = new Array(end - begin);

    for (var offset = begin, i = 0; offset < end; ++i, ++offset) {
      bytes[i] = byteCharacters[offset].charCodeAt(0);
    }

    byteArrays[sliceIndex] = new Uint8Array(bytes);
  }

  return new Blob(byteArrays, { type: contentType });
};